@app.route('/api/dispatch/webhook', methods=['POST'])
def dispatch_webhook():
    """
    Handle incoming call dispatch webhook.
    """
    data = request.get_json()
    
    # Validate required fields
    if not data:
        return jsonify({
            "error": "bad_request",
            "message": "Request body must be JSON"
        }), 400
    
    call_id = data.get('call_id')
    caller_from = data.get('from')
    primary_busy = data.get('primary_busy', False)
    
    if not call_id:
        return jsonify({
            "error": "missing_field",
            "message": "call_id is required"
        }), 400
    
    if not caller_from:
        return jsonify({
            "error": "missing_field",
            "message": "from is required"
        }), 400
    
    # Dispatch logic
    if not primary_busy:
        return jsonify({
            "action": "connect_primary",
            "call_id": call_id,
            "note": "STUB: Would connect to primary agent via Twilio"
        })
    
    # Check for n8n integration
    n8n_webhook_url = os.getenv('N8N_DISPATCH_WEBHOOK_URL')
    
    if n8n_webhook_url:
        try:
            print(f"Forwarding to n8n: {n8n_webhook_url}")
            n8n_response = requests.post(
                n8n_webhook_url,
                json=data,
                timeout=10
            )
            
            if n8n_response.status_code == 200:
                return jsonify(n8n_response.json())
            else:
                print(f"n8n Error: {n8n_response.status_code} - {n8n_response.text}")
                # Fallback to local logic on error
        except Exception as e:
            print(f"n8n Connection Failed: {e}")
            # Fallback to local logic on exception

    # Fallback to AI - deterministic response for demos
    metadata = data.get('metadata', {})
    emergency_type = metadata.get('emergency_type', 'default')
    
    response = DEMO_FALLBACK_RESPONSES.get(
        emergency_type, 
        DEMO_FALLBACK_RESPONSES['default']
    ).copy()
    
    response['call_id'] = call_id
    response['note'] = "STUB: Replace with actual Twilio + AI integration"
    
    return jsonify(response)

