@app.route('/api/bots/generate', methods=['POST'])
def generate_bot_endpoint():
    """
    Generate a bot from natural language description.
    
    Request body:
    {
        "description": "I run a phone repair shop. I want a bot..."
    }
    
    Returns:
    - Generated bot config
    - Unique business_id
    - WhatsApp link to share
    - QR code URL
    """
    data = request.json
    description = data.get('description', '')
    
    if not description or len(description) < 20:
        return jsonify({
            "error": "Please provide a detailed description (at least 20 characters)"
        }), 400
    
    print(f"[BOT GENERATOR] Creating bot from description: {description[:100]}...")
    
    # Generate bot config using AI
    ai_config = generate_bot_from_description(description)
    
    if "error" in ai_config:
        return jsonify(ai_config), 500
    
    # Create unique business ID
    business_id = generate_unique_id()
    
    # Generate WhatsApp links
    links = generate_whatsapp_link(business_id)
    
    # Prepare full config to save
    full_config = {
        "business_id": business_id,
        "original_description": description,
        "bot_name": ai_config.get("bot_name", "Assistant"),
        "business_info": ai_config.get("business_info", {}),
        "capabilities": ai_config.get("capabilities", []),
        "welcome_message": ai_config.get("welcome_message", "Hello! How can I help you?"),
        "conversation_flow": ai_config.get("conversation_flow", []),
        "system_prompt": ai_config.get("system_prompt", ""),
        "whatsapp_link": links["whatsapp_link"],
        "qr_code_url": links["qr_code_url"],
        "created_at": datetime.now().isoformat(),
        "status": "active"
    }
    
    # Save config
    configs = load_configs()
    configs[business_id] = full_config
    save_configs(configs)
    
    print(f"[BOT GENERATOR] Bot created successfully: {business_id}")
    
    return jsonify({
        "success": True,
        "business_id": business_id,
        "bot_name": full_config["bot_name"],
        "capabilities": full_config["capabilities"],
        "welcome_message": full_config["welcome_message"],
        "whatsapp_link": links["whatsapp_link"],
        "qr_code_url": links["qr_code_url"],
        "message": "Your bot is ready! Share the WhatsApp link with your customers."
    })


@app.route('/api/bots/create', methods=['POST'])
def create_bot_from_form():
    """Create a new bot from structured form data"""
    data = request.json
    
    business_name = data.get('business_name', '').strip()
    if not business_name:
        return jsonify({"error": "Business name is required"}), 400
    
    capabilities = data.get('capabilities', [])
    if not capabilities:
        return jsonify({"error": "Please select at least one capability"}), 400
    
    # Generate unique business ID
    business_id = generate_unique_id()
    
    # Generate slug from business name (e.g., "Phone Fix" -> "phone_fix")
    slug = re.sub(r'[^a-zA-Z0-9]+', '_', business_name.lower()).strip('_')
    
    # Map capability codes to readable names
    capability_map = {
        'appointments': 'Book Appointments',
        'price_quotes': 'Provide Price Quotes',
        'orders': 'Take Orders',
        'order_tracking': 'Track Orders',
        'reservations': 'Table Reservations',
        'menu': 'Show Menu/Catalog',
        'faq': 'Answer FAQs',
        'complaints': 'Handle Complaints',
        'support': 'Customer Support',
        'returns': 'Process Returns'
    }
    
    capability_names = [capability_map.get(c, c) for c in capabilities]
    
    # Build system prompt from form data
    bot_name = f"{business_name} Bot"
    
    system_prompt_parts = [
        "CRITICAL: You MUST stay in character at all times. Never break character or say you are an AI.",
        "",
        f"You are {bot_name}, the friendly assistant for {business_name}.",
    ]
    
    if data.get('business_type'):
        system_prompt_parts.append(f"We are a {data['business_type']} business.")
    
    system_prompt_parts.extend([
        "",
        "YOUR CAPABILITIES:",
    ])
    
    for cap in capability_names:
        system_prompt_parts.append(f"- {cap}")
    
    system_prompt_parts.extend([
        "",
        "BUSINESS INFORMATION:",
    ])
    
    if data.get('contact_phone'):
        system_prompt_parts.append(f"- Contact: {data['contact_phone']}")
    if data.get('business_hours'):
        system_prompt_parts.append(f"- Hours: {data['business_hours']}")
    if data.get('location'):
        system_prompt_parts.append(f"- Location: {data['location']}")
    
    if data.get('description'):
        system_prompt_parts.extend([
            "",
            f"About us: {data['description']}"
        ])
    
    system_prompt_parts.extend([
        "",
        "RULES:",
        f"- You ARE {bot_name}. NEVER say you are an AI or language model.",
        "- If asked for contact info, provide the business information above.",
        "- Keep responses brief and helpful (under 200 words).",
        "- Ask one question at a time.",
        "- Be friendly and professional."
    ])
    
    system_prompt = "\n".join(system_prompt_parts)
    
    # Generate WhatsApp links
    links = generate_whatsapp_link(business_id)
    
    # Prepare full config
    full_config = {
        "business_id": business_id,
        "slug": slug,
        "bot_name": bot_name,
        "business_info": {
            "name": business_name,
            "type": data.get('business_type', ''),
            "phone": data.get('contact_phone', ''),
            "hours": data.get('business_hours', ''),
            "location": data.get('location', ''),
        },
        "capabilities": capability_names,
        "system_prompt": system_prompt,
        "whatsapp_link": links["whatsapp_link"],
        "qr_code_url": links["qr_code_url"],
        "created_at": datetime.now().isoformat(),
        "status": "active"
    }
    
    # Save config
    # OLD: configs = load_configs()
    # OLD: configs[business_id] = full_config
    # OLD: save_configs(configs)
    
    # NEW: Save directly to Supabase via helper
    save_single_config(business_id, full_config)
    
    print(f"[BOT CREATOR] Bot created: {business_id} ({slug}) for {business_name}")
    
    return jsonify({
        "success": True,
        "business_id": business_id,
        "slug": slug,
        "bot_name": bot_name,
        "capabilities": capability_names,
        "whatsapp_link": links["whatsapp_link"],
        "qr_code_url": links["qr_code_url"]
    })


@app.route('/api/bots/<business_id>', methods=['GET'])
def get_bot_config(business_id):
    """Get full bot configuration"""
    configs = load_configs()
    
    # Try by ID first
    if business_id in configs:
        return jsonify(configs[business_id])
        
    # Try by slug
    for config in configs.values():
        if config.get('slug') == business_id:
            return jsonify(config)

    return jsonify({"error": "Business not found"}), 404


@app.route('/api/bots/prompt', methods=['GET'])
def get_bot_prompt():
    """
    Get dynamic system prompt for n8n AI Agent
    Expects 'business_id' query param (can be ID or slug)
    """
    business_id = request.args.get('business_id', 'default')
    print(f"[PROMPT FETCH] Request for business: {business_id}")
    
    configs = load_configs()
    config = None
    
    # 1. Try direct ID match
    if business_id in configs:
        config = configs[business_id]
    else:
        # 2. Try searching by slug
        for c in configs.values():
            if c.get('slug') == business_id:
                config = c
                break
    
    if not config:
        # 3. Fallback to default
        print(f"[PROMPT FETCH] Business {business_id} not found, using default")
        config = {
            "bot_name": "Assistant",
            "business_id": "default",
            "system_prompt": "You are CivicAid Assistant. Help users with general inquiries. Be helpful and friendly."
        }
        
    system_prompt = generate_system_prompt(config)
    
    return jsonify({
        "business_id": config.get('business_id', 'default'),
        "bot_name": config.get('bot_name', 'Assistant'),
        "system_prompt": system_prompt
    })


